parameters.jsCode:
// Parse script from LLM response
const response = $input.first().json;
const prevData = $('Update Status').first().json;

try {
  const content = response.choices[0].message.content;
  const scriptData = JSON.parse(content);

  return [{
    json: {
      content_idea_id: prevData.id || $('Normalize Idea').first().json.content_idea_id,
      hook: scriptData.hook,
      script: scriptData.script,
      cta: scriptData.cta,
      estimated_duration: scriptData.estimated_duration_seconds || 45,
      suggested_broll: scriptData.suggested_broll || [],
      pillar: $('Normalize Idea').first().json.pillar
    }
  }];
} catch (error) {
  throw new Error(`Failed to parse script: ${error.message}`);
}

// Prepare B-roll search queries
const asset = $input.first().json;
const parseData = $('Parse Script').first().json;
const script = $('Save Script').first().json;
const suggestedBroll = parseData.suggested_broll || [];

// Default searches if none provided
const defaultSearches = {
  market_intelligence: ['real estate market', 'house exterior', 'city skyline aerial'],
  educational_tips: ['home inspection', 'signing contract documents', 'house tour interior'],
  lifestyle_local: ['neighborhood street', 'local coffee shop', 'park families'],
  brand_humanization: ['business professional', 'handshake meeting', 'modern office']
};

const pillar = parseData.pillar || 'educational_tips';
const searches = suggestedBroll.length > 0 ? suggestedBroll : defaultSearches[pillar];

return searches.slice(0, 3).map((query, index) => ({
  json: {
    asset_id: asset.id,
    script_id: script.id,
    content_idea_id: parseData.content_idea_id,
    scene_index: index,
    search_query: query
  }
}));

// Select best video from Pexels results
const items = $input.all();
const results = [];

for (const item of items) {
  const videos = item.json.videos || [];
  const prevItem = $('Prepare B-Roll Searches').all().find(
    p => p.json.scene_index === item.json.scene_index
  ) || $('Prepare B-Roll Searches').first();

  if (videos.length === 0) {
    results.push({
      json: {
        asset_id: prevItem.json.asset_id,
        script_id: prevItem.json.script_id,
        content_idea_id: prevItem.json.content_idea_id,
        scene_index: prevItem.json.scene_index,
        video_found: false
      }
    });
    continue;
  }

  const video = videos[0];
  const videoFiles = video.video_files || [];
  const selectedFile = videoFiles.find(f => f.quality === 'hd') || videoFiles[0];

  results.push({
    json: {
      asset_id: prevItem.json.asset_id,
      script_id: prevItem.json.script_id,
      content_idea_id: prevItem.json.content_idea_id,
      scene_index: prevItem.json.scene_index,
      video_found: true,
      pexels_id: video.id,
      video_url: selectedFile?.link,
      duration: video.duration
    }
  });
}

return results;

// Aggregate B-roll results and prepare for voice generation
const items = $input.all();
const firstItem = $('Select Videos').first().json;

// Get script and asset data
const scriptData = $('Save Script').first().json;
const assetData = $('Create Asset Record').first().json;
const parseData = $('Parse Script').first().json;

// Clean script for TTS
let text = parseData.script || '';
text = text.replace(/\[PAUSE\]/gi, '...');
text = text.replace(/\*(.*?)\*/g, '$1');
text = text.replace(/\s+/g, ' ').trim();

if (parseData.cta) {
  text = text + ' ... ' + parseData.cta;
}

return [{
  json: {
    asset_id: assetData.id,
    script_id: scriptData.id,
    content_idea_id: parseData.content_idea_id,
    text_for_tts: text,
    broll_paths: items.filter(i => i.json.fileName).map((i, idx) => 
      `/home/node/assets/videos/${scriptData.id}_scene${idx}.mp4`
    )
  }
}];

const { execSync } = require('child_process');
const ttsData = $('Prepare TTS Text').first().json;
const audioPath = `/home/node/assets/audio/${ttsData.script_id}_voice.mp3`;

try {
  const result = execSync(`ffprobe -v quiet -show_entries format=duration -of csv=p=0 "${audioPath}"`, { encoding: 'utf-8' });
  return [{ json: { stdout: result.trim(), success: true } }];
} catch (error) {
  return [{ json: { stdout: '45', success: false, error: error.message } }];
}

// Parse duration and prepare for HeyGen
const durationResult = $input.first().json;
const ttsData = $('Prepare TTS Text').first().json;

let duration = 45;
try {
  duration = parseFloat(durationResult.stdout.trim()) || 45;
} catch (e) {}

return [{
  json: {
    asset_id: ttsData.asset_id,
    script_id: ttsData.script_id,
    content_idea_id: ttsData.content_idea_id,
    voice_path: `/home/node/assets/audio/${ttsData.script_id}_voice.mp3`,
    duration_seconds: Math.round(duration * 10) / 10,
    broll_paths: ttsData.broll_paths
  }
}];

// Extract HeyGen video ID and initialize retry counter
const response = $input.first().json;
const prevData = $('Prepare HeyGen Data').first().json;

if (!response.data?.video_id) {
  throw new Error(`HeyGen error: ${JSON.stringify(response)}`);
}

return [{
  json: {
    ...prevData,
    heygen_video_id: response.data.video_id,
    retry_count: 0,
    max_retries: 20
  }
}];

// Increment retry counter and check max
const prevData = $('Extract Video ID').first().json;
const checkData = $input.first().json;
const retryCount = (prevData.retry_count || 0) + 1;
const maxRetries = prevData.max_retries || 20;

if (retryCount > maxRetries) {
  throw new Error(`HeyGen timeout: Video ${prevData.heygen_video_id} did not complete after ${maxRetries} retries (${maxRetries * 15 / 60} minutes). Last status: ${checkData.data?.status}`);
}

return [{
  json: {
    ...prevData,
    retry_count: retryCount,
    last_status: checkData.data?.status
  }
}];

// Build FFmpeg command for video compositing
const prevData = $('Extract Video ID').first().json;
const scriptId = prevData.script_id;

const avatarPath = `/home/node/assets/avatar/${scriptId}_avatar.mp4`;
const brollPaths = prevData.broll_paths || [];
const outputPath = `/home/node/assets/output/${scriptId}_combined.mp4`;

// Build FFmpeg filter complex
let inputs = `-i ${avatarPath}`;
let filterComplex = '';

if (brollPaths.length > 0) {
  // Add B-roll inputs
  brollPaths.forEach(path => {
    inputs += ` -i ${path}`;
  });

  // Concat B-roll clips
  const brollInputs = brollPaths.map((_, i) => `[${i + 1}:v]`).join('');
  filterComplex = `${brollInputs}concat=n=${brollPaths.length}:v=1:a=0[broll];`;
  filterComplex += `[broll]scale=1080:1920:force_original_aspect_ratio=increase,crop=1080:1920[broll_scaled];`;
  filterComplex += `[0:v]chromakey=0x00FF00:0.1:0.2[avatar_keyed];`;
  filterComplex += `[broll_scaled][avatar_keyed]overlay=(W-w)/2:H-h-100:shortest=1[outv]`;
} else {
  // No B-roll - solid background
  filterComplex = `[0:v]chromakey=0x00FF00:0.1:0.2[avatar_keyed];`;
  filterComplex += `color=c=#1a1a2e:s=1080x1920:d=120[bg];`;
  filterComplex += `[bg][avatar_keyed]overlay=(W-w)/2:H-h-100:shortest=1[outv]`;
}

const ffmpegCmd = `ffmpeg -y ${inputs} -filter_complex "${filterComplex}" -map "[outv]" -map 0:a? -c:v libx264 -preset fast -crf 23 -c:a aac -b:a 128k -movflags +faststart ${outputPath}`;

return [{
  json: {
    asset_id: prevData.asset_id,
    script_id: scriptId,
    content_idea_id: prevData.content_idea_id,
    ffmpeg_command: ffmpegCmd,
    output_path: outputPath
  }
}];

const { execSync } = require('child_process');
const ffmpegCmd = $input.first().json.ffmpeg_command;

try {
  const result = execSync(ffmpegCmd, { encoding: 'utf-8', timeout: 300000 });
  return [{ json: { stdout: result, exitCode: 0, success: true } }];
} catch (error) {
  if (error.status !== undefined) {
    return [{ json: { stdout: error.stdout || '', stderr: error.stderr || '', exitCode: error.status, success: error.status === 0 } }];
  }
  return [{ json: { stdout: '', stderr: error.message, exitCode: 1, success: false } }];
}

// Prepare data for Whisper transcription
const ffmpegData = $('Build FFmpeg Command').first().json;
const ttsData = $('Prepare TTS Text').first().json;

return [{
  json: {
    asset_id: ffmpegData.asset_id,
    script_id: ffmpegData.script_id,
    content_idea_id: ffmpegData.content_idea_id,
    combined_video_path: ffmpegData.output_path,
    audio_path: `/home/node/assets/audio/${ffmpegData.script_id}_voice.mp3`
  }
}];

// Extract SRT content from Whisper response
const response = $input.first().json;
const prevData = $('Prepare Whisper').first().json;

// Whisper returns SRT as plain text
const srtContent = typeof response === 'string' ? response : (response.text || response.data || JSON.stringify(response));

return [{
  json: {
    asset_id: prevData.asset_id,
    script_id: prevData.script_id,
    content_idea_id: prevData.content_idea_id,
    combined_video_path: prevData.combined_video_path,
    srt_content: srtContent
  }
}];

// Build FFmpeg caption burn command
const prevData = $('Parse Whisper Response').first().json;
const scriptId = prevData.script_id;

const inputPath = prevData.combined_video_path;
const srtPath = `/home/node/assets/captions/${scriptId}_captions.srt`;
const outputPath = `/home/node/assets/output/${scriptId}_final.mp4`;

// Styled subtitles for social media
const subtitleFilter = `subtitles=${srtPath}:force_style='FontName=Arial,FontSize=28,PrimaryColour=&HFFFFFF,OutlineColour=&H000000,Outline=3,Shadow=1,Alignment=2,MarginV=80'`;

const ffmpegCmd = `ffmpeg -y -i ${inputPath} -vf "${subtitleFilter}" -c:v libx264 -preset fast -crf 23 -c:a copy ${outputPath}`;

return [{
  json: {
    asset_id: prevData.asset_id,
    script_id: scriptId,
    content_idea_id: prevData.content_idea_id,
    ffmpeg_command: ffmpegCmd,
    final_video_path: outputPath
  }
}];

const { execSync } = require('child_process');
const ffmpegCmd = $input.first().json.ffmpeg_command;

try {
  const result = execSync(ffmpegCmd, { encoding: 'utf-8', timeout: 300000 });
  return [{ json: { stdout: result, exitCode: 0, success: true } }];
} catch (error) {
  if (error.status !== undefined) {
    return [{ json: { stdout: error.stdout || '', stderr: error.stderr || '', exitCode: error.status, success: error.status === 0 } }];
  }
  return [{ json: { stdout: '', stderr: error.message, exitCode: 1, success: false } }];
}

// Handle caption failure - update asset status and throw error
const captionData = $('Build Caption Cmd').first().json;
const errorMsg = $input.first().json.stderr || 'Caption burn failed';

throw new Error(`Caption failed for asset ${captionData.asset_id}: ${errorMsg}`);

// Prepare publishing data with platform-specific captions
const captionData = $('Build Caption Cmd').first().json;
const parseData = $('Parse Script').first().json;

const pillarHashtags = {
  market_intelligence: ['#realestate', '#housingmarket', '#marketupdate', '#homeprices'],
  educational_tips: ['#realestatetips', '#homebuying101', '#firsttimehomebuyer', '#realtortips'],
  lifestyle_local: ['#localrealestate', '#dreamhome', '#neighborhoodguide', '#homesweethome'],
  brand_humanization: ['#realtor', '#realtorlife', '#behindthescenes', '#realestateagent']
};

const hashtags = pillarHashtags[parseData.pillar] || pillarHashtags.educational_tips;
const caption = `${parseData.hook}\n\n${parseData.cta}\n\n${hashtags.join(' ')}`;

return [{
  json: {
    asset_id: captionData.asset_id,
    script_id: captionData.script_id,
    content_idea_id: captionData.content_idea_id,
    final_video_path: captionData.final_video_path,
    caption: caption,
    platforms: ['tiktok', 'instagram', 'youtube_shorts']
  }
}];

// Process publish response and map to individual platform columns
const response = $input.first().json;
const publishData = $('Prepare Publish Data').first().json;

const posts = response.posts || response.data?.posts || [];

// Map platform posts to individual columns matching backend schema
const platformData = {
  tiktok_url: null, tiktok_id: null,
  ig_url: null, ig_id: null,
  yt_url: null, yt_id: null,
  linkedin_url: null, linkedin_id: null,
  x_url: null, x_id: null,
  facebook_url: null, facebook_id: null,
  threads_url: null, threads_id: null,
  pinterest_url: null, pinterest_id: null
};

posts.forEach(post => {
  const platform = post.platform?.toLowerCase();
  if (platform === 'tiktok') {
    platformData.tiktok_url = post.url;
    platformData.tiktok_id = post.id;
  } else if (platform === 'instagram') {
    platformData.ig_url = post.url;
    platformData.ig_id = post.id;
  } else if (platform === 'youtube' || platform === 'youtube_shorts') {
    platformData.yt_url = post.url;
    platformData.yt_id = post.id;
  } else if (platform === 'linkedin') {
    platformData.linkedin_url = post.url;
    platformData.linkedin_id = post.id;
  } else if (platform === 'twitter' || platform === 'x') {
    platformData.x_url = post.url;
    platformData.x_id = post.id;
  } else if (platform === 'facebook') {
    platformData.facebook_url = post.url;
    platformData.facebook_id = post.id;
  } else if (platform === 'threads') {
    platformData.threads_url = post.url;
    platformData.threads_id = post.id;
  } else if (platform === 'pinterest') {
    platformData.pinterest_url = post.url;
    platformData.pinterest_id = post.id;
  }
});

return [{
  json: {
    asset_id: publishData.asset_id,
    content_idea_id: publishData.content_idea_id,
    ...platformData,
    published_at: new Date().toISOString()
  }
}];
