// Parse incoming niche/platform parameters
const body = $input.first().json.body || $input.first().json;

// Defaults
const niche = body.niche || 'real estate';
const hashtags = body.hashtags || ['realestate', 'realtor', 'homebuying', 'firsttimehomebuyer'];
const platforms = body.platforms || ['tiktok', 'instagram'];
const timeRange = body.time_range || '24h';
const resultsPerPlatform = body.results_per_platform || 50;

// Build search terms from niche
const nicheTerms = niche.split(/[,;]+/).map(t => t.trim()).filter(t => t);

return [{
  json: {
    niche,
    nicheTerms,
    hashtags,
    platforms,
    timeRange,
    resultsPerPlatform,
    scrapeStartedAt: new Date().toISOString()
  }
}];

// Normalize scraped items from different platforms
const items = $input.all();
const params = $('Parse Scrape Params').first().json;
const normalized = [];

for (const item of items) {
  const data = item.json;
  if (!data || typeof data !== 'object') continue;

  // Skip empty arrays or merge placeholders
  if (Array.isArray(data) && data.length === 0) continue;

  // TikTok format
  if (data.webVideoUrl || data.videoUrl) {
    normalized.push({
      platform: 'tiktok',
      url: data.webVideoUrl || data.videoUrl,
      title: data.text || data.desc || '',
      views: data.playCount || 0,
      likes: data.diggCount || 0,
      comments: data.commentCount || 0,
      shares: data.shareCount || 0,
      engagement: (data.diggCount || 0) + (data.commentCount || 0) + (data.shareCount || 0),
      author: data.authorMeta?.name || data.author || '',
      created: data.createTime || null,
      thumbnail: data.covers?.default || data.videoMeta?.coverUrl || ''
    });
  }
  // Instagram Reels format
  else if (data.url && (data.url.includes('instagram') || data.type === 'Reel')) {
    normalized.push({
      platform: 'instagram',
      url: data.url || data.inputUrl,
      title: data.caption || data.text || '',
      views: data.playCount || data.videoPlayCount || 0,
      likes: data.likesCount || data.likes || 0,
      comments: data.commentsCount || data.comments || 0,
      shares: 0,
      engagement: (data.likesCount || data.likes || 0) + (data.commentsCount || data.comments || 0),
      author: data.ownerUsername || data.author || '',
      created: data.timestamp || null,
      thumbnail: data.displayUrl || data.thumbnail || ''
    });
  }
  // YouTube format
  else if (data.url && data.url.includes('youtube')) {
    normalized.push({
      platform: 'youtube',
      url: data.url,
      title: data.title || '',
      views: data.viewCount || 0,
      likes: data.likes || 0,
      comments: data.comments || 0,
      shares: 0,
      engagement: (data.likes || 0) + (data.comments || 0),
      author: data.channelName || data.author || '',
      created: data.uploadDate || null,
      thumbnail: data.thumbnailUrl || ''
    });
  }
}

// Sort by engagement (highest first)
normalized.sort((a, b) => b.engagement - a.engagement);

// Add metadata
const output = {
  niche: params.niche,
  platforms: params.platforms,
  scrapedAt: params.scrapeStartedAt,
  totalResults: normalized.length,
  results: normalized
};

return [{ json: output }];

