// Parse LLM response and format for API/UI
const response = $input.first().json;
const normalizedData = $('Normalize Data').first().json;

try {
  const content = response.choices[0].message.content;
  const parsed = JSON.parse(content);
  const items = parsed.analyzed || parsed.items || [];

  // Filter for viral_score >= 6 and adaptable = true
  const filtered = items.filter(item => 
    item.viral_score >= 6 && item.adaptable !== false
  );

  return [{
    json: {
      success: true,
      niche: normalizedData.niche,
      scrapedAt: normalizedData.scrapedAt,
      totalScraped: normalizedData.totalResults,
      analyzedCount: filtered.length,
      trends: filtered
    }
  }];
} catch (error) {
  return [{
    json: {
      success: false,
      error: error.message,
      niche: normalizedData.niche,
      scrapedAt: normalizedData.scrapedAt,
      totalScraped: normalizedData.totalResults,
      analyzedCount: 0,
      trends: []
    }
  }];
}

// Process trends, save to DB, and guarantee response
const data = $input.first().json;
const savedItems = [];
let savedCount = 0;

// 1. Handle Empty/Error Case
if (!data.success || !data.trends || data.trends.length === 0) {
  return [{
    json: {
      success: true, // Return success so UI doesn't crash
      niche: data.niche,
      scrapedAt: data.scrapedAt,
      totalScraped: data.totalScraped || 0,
      analyzedCount: 0,
      savedCount: 0,
      message: 'No suitable trends found to save',
      trends: []
    }
  }];
}

// 2. Process & Save Each Trend
for (const trend of data.trends) {
  try {
    // Infer platform
    let platform = trend.platform;
    const url = trend.url || '';
    if (!platform || platform === 'unknown') {
      if (url.includes('tiktok.com')) platform = 'tiktok';
      else if (url.includes('instagram.com')) platform = 'instagram';
      else if (url.includes('youtube.com') || url.includes('youtu.be')) platform = 'youtube';
    }

    // Construct Payload
    const payload = {
      source_url: url,
      source_platform: (platform ? platform.toLowerCase() : 'unknown'),
      original_text: trend.title || '',
      pillar: trend.pillar || 'educational_tips',
      viral_score: trend.viral_score || 7,
      suggested_hook: trend.suggested_hook || '',
      why_viral: trend.why_viral || '',
      status: 'pending'
    };

    // Send to Backend
    // Using this.helpers.request (n8n built-in)
    const response = await this.helpers.request({
      method: 'POST',
      url: 'http://backend:8000/api/content-ideas',
      body: payload,
      json: true
    });

    if (response && response.id) {
        savedItems.push(response);
        savedCount++;
    }
  } catch (err) {
    console.log(`Failed to save trend: ${err.message}`);
    // Continue to next item, don't stop workflow
  }
}

// 3. Return Summary Response
return [{
  json: {
    success: true,
    niche: data.niche,
    scrapedAt: data.scrapedAt,
    totalScraped: data.totalScraped,
    analyzedCount: data.analyzedCount,
    savedCount: savedCount,
    message: `Saved ${savedCount} content ideas to database`,
    trends: savedItems
  }
}];

// Normalize and validate content idea
const items = $input.all();
let idea = null;

for (const item of items) {
  const data = item.json;
  if (Array.isArray(data) && data.length > 0) {
    idea = data[0];
    break;
  } else if (data && data.id) {
    idea = data;
    break;
  }
}

if (!idea || !idea.id) {
  return [{ json: { hasContent: false, message: 'No approved content found' } }];
}

return [{
  json: {
    hasContent: true,
    content_idea_id: idea.id,
    original_text: idea.original_text,
    pillar: idea.pillar,
    suggested_hook: idea.suggested_hook,
    source_platform: idea.source_platform,
    source_url: idea.source_url
  }
}];

// Parse script from LLM response
const response = $input.first().json;
const prevData = $('Update Status').first().json;

try {
  const content = response.choices[0].message.content;
  const scriptData = JSON.parse(content);

  return [{
    json: {
      content_idea_id: prevData.id || $('Normalize Idea').first().json.content_idea_id,
      hook: scriptData.hook,
      script: scriptData.script,
      cta: scriptData.cta,
      estimated_duration: scriptData.estimated_duration_seconds || 45,
      suggested_broll: scriptData.suggested_broll || [],
      pillar: $('Normalize Idea').first().json.pillar
    }
  }];
} catch (error) {
  throw new Error(`Failed to parse script: ${error.message}`);
}

// Prepare B-roll search queries
const asset = $input.first().json;
const parseData = $('Parse Script').first().json;
const script = $('Save Script').first().json;
const suggestedBroll = parseData.suggested_broll || [];

// Default searches if none provided
const defaultSearches = {
  market_intelligence: ['real estate market', 'house exterior', 'city skyline aerial'],
  educational_tips: ['home inspection', 'signing contract documents', 'house tour interior'],
  lifestyle_local: ['neighborhood street', 'local coffee shop', 'park families'],
  brand_humanization: ['business professional', 'handshake meeting', 'modern office']
};

const pillar = parseData.pillar || 'educational_tips';
const searches = suggestedBroll.length > 0 ? suggestedBroll : defaultSearches[pillar];

return searches.slice(0, 3).map((query, index) => ({
  json: {
    asset_id: asset.id,
    script_id: script.id,
    content_idea_id: parseData.content_idea_id,
    scene_index: index,
    search_query: query
  }
}));

// Select best video from Pexels results
const items = $input.all();
const results = [];

for (const item of items) {
  const videos = item.json.videos || [];
  const prevItem = $('Prepare B-Roll Searches').all().find(
    p => p.json.scene_index === item.json.scene_index
  ) || $('Prepare B-Roll Searches').first();

  if (videos.length === 0) {
    results.push({
      json: {
        asset_id: prevItem.json.asset_id,
        script_id: prevItem.json.script_id,
        content_idea_id: prevItem.json.content_idea_id,
        scene_index: prevItem.json.scene_index,
        video_found: false
      }
    });
    continue;
  }

  const video = videos[0];
  const videoFiles = video.video_files || [];
  const selectedFile = videoFiles.find(f => f.quality === 'hd') || videoFiles[0];

  results.push({
    json: {
      asset_id: prevItem.json.asset_id,
      script_id: prevItem.json.script_id,
      content_idea_id: prevItem.json.content_idea_id,
      scene_index: prevItem.json.scene_index,
      video_found: true,
      pexels_id: video.id,
      video_url: selectedFile?.link,
      duration: video.duration
    }
  });
}

return results;

const fs = require('fs');
const item = $input.item;
// Reconstruct filename using the same logic as the previous expression
// Use try-catch to handle potential expression failures safely
try {
  const scriptId = $('Select Videos').first().json.script_id;
  const sceneIndex = $('Select Videos').first().json.scene_index;
  const fileName = `/home/node/.n8n-files/assets/videos/${scriptId}_scene${sceneIndex}.mp4`;

  if (item.binary && item.binary.data) {
    fs.writeFileSync(fileName, Buffer.from(item.binary.data.data, 'base64'));
    item.json.saved_path = fileName;
  }
} catch (e) {
  // If Select Videos logic is complex, fall back to current item properties if available
  // or log error
  console.log('Error writing file: ' + e.message);
  throw e;
}
return item;

// Aggregate B-roll results and prepare for voice generation
const brollItems = $('Merge B-Roll').all();
const charConfig = $input.first().json || {};

const firstItem = $('Select Videos').first().json;

// Get script and asset data
const scriptData = $('Save Script').first().json;
const assetData = $('Create Asset Record').first().json;
const parseData = $('Parse Script').first().json;

// Clean script for TTS
let text = parseData.script || '';
text = text.replace(/\[PAUSE\]/gi, '...');
text = text.replace(/\*(.*?)\*/g, '$1');
text = text.replace(/\s+/g, ' ').trim();

if (parseData.cta) {
  text = text + ' ... ' + parseData.cta;
}

// Robust Voice ID Selection
let voiceId = charConfig.voice_id;
if (!voiceId || typeof voiceId !== 'string' || voiceId.trim().length < 5) {
    console.log(`Invalid or missing voice_id '${voiceId}', falling back to Rachel.`);
    voiceId = '21m00Tcm4TlvDq8ikWAM'; // Rachel
} else {
    voiceId = voiceId.trim();
}

return [{
  json: {
    asset_id: assetData.id,
    script_id: scriptData.id,
    content_idea_id: parseData.content_idea_id,
    text_for_tts: text,
    voice_id: voiceId,
    avatar_id: charConfig.avatar_id || 'Kristin_pubblic_3_20240108',
    avatar_type: charConfig.avatar_type || 'avatar',
    config_image_url: charConfig.image_url,
    broll_paths: brollItems.filter(i => i.json.fileName).map((i, idx) => 
      `/home/node/.n8n-files/assets/videos/${scriptData.id}_scene${idx}.mp4`
    )
  }
}];

const fs = require('fs');
try {
  const sourceNode = $('Prepare TTS Text').first();
  const sourceData = sourceNode.json;
  const scriptId = sourceData.script_id;
  const filePath = `/home/node/.n8n-files/assets/audio/${scriptId}_voice.mp3`;
  const exists = fs.existsSync(filePath);

  // MERGE source data so voice_id/text_for_tts persist to the Generator
  return [{ 
      json: { 
          ...sourceData,
          exists, 
          filePath, 
          script_id: scriptId,
          // Explicitly ensure voice_id is passed if it exists in source
          voice_id: sourceData.voice_id 
      } 
  }];
} catch (e) {
  return [{ json: { exists: false, error: e.message } }];
}

const { execSync } = require('child_process');
const ttsData = $('Prepare TTS Text').first().json;
const audioPath = `/home/node/.n8n-files/assets/audio/${ttsData.script_id}_voice.mp3`;

try {
  const result = execSync(`ffprobe -v quiet -show_entries format=duration -of csv=p=0 "${audioPath}"`, { encoding: 'utf-8' });
  return [{ json: { stdout: result.trim(), success: true } }];
} catch (error) {
  return [{ json: { stdout: '45', success: false, error: error.message } }];
}

// Parse inputs
const uploadResult = $('Upload HeyGen Audio').first().json;
const durationResult = $('Get Duration').first().json;
const ttsData = $('Prepare TTS Text').first().json;

// Check for Dynamic Photo Upload
let dynamicPhotoId = null;
try {
  const photoUpload = $('Upload Talking Photo').first();
  if (photoUpload && photoUpload.json && photoUpload.json.data) {
      // v1/talking_photo returns 'talking_photo_id'. v1/asset returns 'id'.
      // We prioritize talking_photo_id.
      dynamicPhotoId = photoUpload.json.data.talking_photo_id || photoUpload.json.data.id;
  }
} catch(e) {}

let duration = 45;
try {
  duration = parseFloat(durationResult.stdout.trim()) || 45;
} catch (e) {}

// Avatar Logic
let avatarId = ttsData.avatar_id;
let avatarType = ttsData.avatar_type;

// If static_image, we MUST have a dynamic ID now
if (avatarId === 'static_image') {
    if (dynamicPhotoId) {
        avatarType = 'talking_photo';
        avatarId = dynamicPhotoId;
    } else {
        // Fallback
        console.log('Missing dynamic photo upload, falling back to default avatar');
        avatarId = 'Anna_public_3_20240108';
        avatarType = 'avatar';
    }
}

return [{
  json: {
    asset_id: ttsData.asset_id,
    script_id: ttsData.script_id,
    content_idea_id: ttsData.content_idea_id,
    voice_path: `/home/node/.n8n-files/assets/audio/${ttsData.script_id}_voice.mp3`,
    duration_seconds: Math.round(duration * 10) / 10,
    broll_paths: ttsData.broll_paths,
    avatar_id: avatarId,
    avatar_type: avatarType,
    audio_asset_id: uploadResult.data.id
  }
}];

const fs = require('fs');
try {
  const sourceNode = $('Prepare HeyGen Data').first();
  const sourceData = sourceNode.json;
  const scriptId = sourceData.script_id;
  const filePath = `/home/node/.n8n-files/assets/avatar/${scriptId}_avatar.mp4`;
  const exists = fs.existsSync(filePath);

  // MERGE source data so audio_asset_id / avatar_id persist to the Generator
  return [{ 
      json: { 
          ...sourceData,
          exists, 
          filePath, 
          script_id: scriptId
      } 
  }];
} catch (e) {
  return [{ json: { exists: false, error: e.message } }];
}

// Extract HeyGen video ID and initialize retry counter
const response = $input.first().json;
const prevData = $('Prepare HeyGen Data').first().json;

if (!response.data?.video_id) {
  throw new Error(`HeyGen error: ${JSON.stringify(response)}`);
}

return [{
  json: {
    ...prevData,
    heygen_video_id: response.data.video_id,
    retry_count: 0,
    max_retries: 20
  }
}];

// Increment retry counter and check max
const prevData = $('Extract Video ID').first().json;
const checkData = $input.first().json;
const retryCount = (prevData.retry_count || 0) + 1;
const maxRetries = prevData.max_retries || 20;

if (retryCount > maxRetries) {
  throw new Error(`HeyGen timeout: Video ${prevData.heygen_video_id} did not complete after ${maxRetries} retries (${maxRetries * 15 / 60} minutes). Last status: ${checkData.data?.status}`);
}

return [{
  json: {
    ...prevData,
    retry_count: retryCount,
    last_status: checkData.data?.status
  }
}];

const fs = require('fs');
const item = $input.item;
try {
  const scriptId = $('Extract Video ID').first().json.script_id;
  const fileName = `/home/node/.n8n-files/assets/avatar/${scriptId}_avatar.mp4`;

  if (item.binary && item.binary.data) {
    fs.writeFileSync(fileName, Buffer.from(item.binary.data.data, 'base64'));
    item.json.saved_path = fileName;
  }
} catch (e) {
  console.log('Error writing avatar file: ' + e.message);
  throw e;
}
return item;

const fs = require('fs');
const extractNode = $('Extract Video ID').first();
// Robust fallback if extraction hasn't happened yet (idempotency)
const sourceData = extractNode ? extractNode.json : $('Prepare HeyGen Data').first().json;
const scriptId = sourceData.script_id;

// --- FILE PATHS ---
// Ensure these match your Docker volume mapping
const avatarPath = `/home/node/.n8n-files/assets/avatar/${scriptId}_avatar.mp4`;
const outputPath = `/home/node/.n8n-files/assets/output/${scriptId}_combined.mp4`;
const musicPath = `/home/node/.n8n-files/assets/music/background_music.mp3`; 
const brollPaths = sourceData.broll_paths || [];

// --- BUILD INPUTS ---
let inputs = `-i ${avatarPath}`; // Input 0: The Avatar Video
let inputCount = 1;

// SAFETY CHECK: Does the music file actually exist?
let musicExists = false;
try {
    if (fs.existsSync(musicPath)) {
        musicExists = true;
    }
} catch (e) {
    console.log("Music check failed, proceeding without music.");
}

let musicIndex = -1;

if (musicExists) {
  // -stream_loop -1 makes the music repeat if the video is longer than the song
  inputs += ` -stream_loop -1 -i ${musicPath}`;
  musicIndex = inputCount;
  inputCount++;
}

// Add B-Roll Inputs
brollPaths.forEach(path => {
  inputs += ` -i ${path}`;
});
const brollStartIndex = inputCount;

// --- BUILD FILTER COMPLEX ---
let fc = '';

// 1. VIDEO COMPOSITING
if (brollPaths.length > 0) {
  // Create a chain of B-Roll clips
  const brollInputs = brollPaths.map((_, i) => `[${brollStartIndex + i}:v]`).join('');

  // Concatenate B-Roll
  fc += `${brollInputs}concat=n=${brollPaths.length}:v=1:a=0[broll_raw];`;

  // Scale and Crop B-Roll to 9:16 (1080x1920) to match Avatar
  fc += `[broll_raw]scale=1080:1920:force_original_aspect_ratio=increase,crop=1080:1920[broll];`;

  // Chroma Key the Avatar (Green Screen Removal)
  fc += `[0:v]chromakey=0x00FF00:0.1:0.2[avatar_keyed];`;

  // Overlay Avatar on top of B-Roll
  fc += `[broll][avatar_keyed]overlay=(W-w)/2:H-h-100:shortest=1[outv];`;
} else {
  // Fallback: If no B-Roll, put Avatar over a solid background
  fc += `[0:v]chromakey=0x00FF00:0.1:0.2[avatar_keyed];`;
  fc += `color=c=#1a1a2e:s=1080x1920:d=120[bg];`;
  fc += `[bg][avatar_keyed]overlay=(W-w)/2:H-h-100:shortest=1[outv];`;
}

// 2. AUDIO MIXING
if (musicExists) {
  // [0:a] is the Voice (Avatar)
  // [musicIndex:a] is the Background Music
  // volume=0.1 drops music to 10% so voice is clear
  // amix mixes them. duration=first cuts audio when video ends.
  fc += `[${musicIndex}:a]volume=0.1[music_low];[0:a][music_low]amix=inputs=2:duration=first[outa]`;
} else {
  // If no music, just pass the voice through as stereo
  fc += `[0:a]aformat=channel_layouts=stereo[outa]`;
}

// --- FINAL COMMAND ---
const ffmpegCmd = `ffmpeg -y ${inputs} -filter_complex "${fc}" -map "[outv]" -map "[outa]" -c:v libx264 -preset fast -crf 23 -c:a aac -b:a 128k -movflags +faststart ${outputPath}`;

return [{
  json: {
    asset_id: sourceData.asset_id,
    script_id: scriptId,
    content_idea_id: sourceData.content_idea_id,
    ffmpeg_command: ffmpegCmd,
    output_path: outputPath
  }
}];

const { execSync } = require('child_process');
const ffmpegCmd = $input.first().json.ffmpeg_command;

try {
  const result = execSync(ffmpegCmd, { encoding: 'utf-8', timeout: 300000 });
  return [{ json: { stdout: result, exitCode: 0, success: true } }];
} catch (error) {
  if (error.status !== undefined) {
    return [{ json: { stdout: error.stdout || '', stderr: error.stderr || '', exitCode: error.status, success: error.status === 0 } }];
  }
  return [{ json: { stdout: '', stderr: error.message, exitCode: 1, success: false } }];
}

// Prepare data for Whisper transcription
const ffmpegData = $('Build FFmpeg Command').first().json;
const ttsData = $('Prepare TTS Text').first().json;

return [{
  json: {
    asset_id: ffmpegData.asset_id,
    script_id: ffmpegData.script_id,
    content_idea_id: ffmpegData.content_idea_id,
    combined_video_path: ffmpegData.output_path,
    audio_path: `/home/node/.n8n-files/assets/audio/${ffmpegData.script_id}_voice.mp3`
  }
}];

const fs = require('fs');
const filePath = $input.first().json.audio_path;

try {
  const buffer = fs.readFileSync(filePath);
  return [{
    json: $input.first().json,
    binary: {
      data: {
        data: buffer.toString('base64'),
        mimeType: 'audio/mpeg',
        fileName: filePath.split('/').pop()
      }
    }
  }];
} catch (error) {
  throw new Error(`Failed to read audio file: ${filePath}. Error: ${error.message}`);
}

// Extract SRT content from Whisper response
const response = $input.first().json;
